import unittest
import sys, os
if os.name == 'posix':
    sep = '/'
else:
    sep = '\\'
sys.path.append(os.getcwd().rsplit(sep,1)[0])
from core import operations
from core.big_number import BigNumber
from operations_tests.util_decorators import timeout


class FunctionTest(unittest.TestCase):

    def setUp(self) -> None:
        self.big_positive_number_1 = BigNumber("19884313212139312328923")
        self.big_positive_number_2 = BigNumber("2920193810920129831098230192830")
        self.result_divide_1 = "146859173"
        self.result_object_1 = BigNumber("146859173")
        self.result_divide_2 = "0"
        self.result_object_2 = BigNumber("0")

        self.big_positive_number_3 = BigNumber("10000000000000000000000000")
        self.big_positive_number_4 = BigNumber("90000000000000000000000000000000000000000000000000")
        self.result_divide_3 = "9000000000000000000000000"
        self.result_object_3 = BigNumber("9000000000000000000000000")
        self.result_divide_4 = "0"
        self.result_object_4 = BigNumber("0")

        self.small_positive_number_1 = BigNumber("73912873")
        self.small_positive_number_2 = BigNumber("831212")
        self.result_divide_5 = "88"
        self.result_object_5 = BigNumber("88")

        self.result_divide_6 = "0"
        self.result_object_6 = BigNumber("0")

        self.small_positive_number_3 = BigNumber("80201")
        self.small_positive_number_4 = BigNumber("932")
        self.result_divide_7 = "86"
        self.result_object_7 = BigNumber("86")

        self.result_divide_8 = "0"
        self.result_object_8 = BigNumber("0")

        self.very_big_positive_number_1 = BigNumber("123" * 100)
        self.very_big_positive_number_2 = BigNumber("256" * 101)
        self.result_divide_9 = '2081'
        self.result_object_9 = BigNumber('2081')

        self.result_divide_10 = '0'
        self.result_object_10 = BigNumber('0')

        self.very_big_positive_number_3 = BigNumber("1938" * 10000)
        self.very_big_positive_number_4 = BigNumber("256" * 10101)
        self.result_divide_11 = ''
        self.result_object_11 = BigNumber('')
        self.result_divide_12 = '0'
        self.result_object_12 = BigNumber('0')

        self.negative_number_1 = BigNumber("-1329320320")
        self.negative_number_2 = BigNumber("-372392729")

        self.positive_number_1 = BigNumber("123903123312")
        self.positive_number_2 = BigNumber("2312931231233123")

        self.wrong_char_number_1 = BigNumber("dq3282023ds")
        self.wrong_char_number_2 = BigNumber("dsajdl3228320das")

    @timeout(3)
    def test_small_positive_numbers(self):

        self.assertEqual((self.small_positive_number_1 / self.small_positive_number_2).number,
                         self.result_divide_5)

        self.assertEqual((self.small_positive_number_1 / self.small_positive_number_2),
                         self.result_object_5)

        self.assertEqual((self.small_positive_number_2 / self.small_positive_number_1).number,
                         self.result_divide_6)

        self.assertEqual((self.small_positive_number_2 / self.small_positive_number_1),
                         self.result_object_6)

        self.assertEqual((self.small_positive_number_3 / self.small_positive_number_4).number,
                         self.result_divide_7)

        self.assertEqual((self.small_positive_number_3 / self.small_positive_number_4),
                         self.result_object_7)

        self.assertEqual((self.small_positive_number_4 / self.small_positive_number_3).number,
                         self.result_divide_8)

        self.assertEqual((self.small_positive_number_4 / self.small_positive_number_3),
                         self.result_object_8)

    def test_zero_division(self):
        with self.assertRaises(ValueError):
            self.big_positive_number_1 / BigNumber('0')

        with self.assertRaises(ValueError):
            self.big_positive_number_3 / BigNumber('0')

    def test_negative_numbers(self):

        with self.assertRaises(ValueError):
            self.negative_number_1 / self.negative_number_2

        with self.assertRaises(ValueError):
            self.negative_number_1 / self.positive_number_1

        with self.assertRaises(ValueError):
            self.negative_number_2 / self.positive_number_1

        with self.assertRaises(ValueError):
            self.negative_number_1 / self.positive_number_2

        with self.assertRaises(ValueError):
            self.negative_number_2 / self.positive_number_2

    def test_wrong_numbers(self):

        with self.assertRaises(ValueError):
            self.wrong_char_number_1 / self.wrong_char_number_2

        with self.assertRaises(ValueError):
            self.wrong_char_number_1 / self.positive_number_1

        with self.assertRaises(ValueError):
            self.wrong_char_number_2 / self.positive_number_1

        with self.assertRaises(ValueError):
            self.wrong_char_number_1 / self.positive_number_2

        with self.assertRaises(ValueError):
            self.wrong_char_number_2 / self.positive_number_2

    @timeout(3)
    def test_big_positive_numbers(self):

        self.assertEqual((self.big_positive_number_2 / self.big_positive_number_1).number,
                         self.result_divide_1)

        self.assertEqual((self.big_positive_number_2 / self.big_positive_number_1),
                         self.result_object_1)

        self.assertEqual((self.big_positive_number_1 / self.big_positive_number_2).number,
                         self.result_divide_2)

        self.assertEqual((self.big_positive_number_1 / self.big_positive_number_2),
                         self.result_object_2)

        self.assertEqual((self.big_positive_number_4 / self.big_positive_number_3).number,
                         self.result_divide_3)

        self.assertEqual((self.big_positive_number_4 / self.big_positive_number_3),
                         self.result_object_3)

        self.assertEqual((self.big_positive_number_3 / self.big_positive_number_4).number,
                         self.result_divide_4)

        self.assertEqual((self.big_positive_number_3 / self.big_positive_number_4),
                         self.result_object_4)

    @timeout(3)
    def test_very_big_positive_numbers(self):

        self.assertEqual((self.very_big_positive_number_2 / self.very_big_positive_number_1).number,
                         self.result_divide_9)

        self.assertEqual((self.very_big_positive_number_2 / self.very_big_positive_number_1),
                         self.result_object_9)

        self.assertEqual((self.very_big_positive_number_1 / self.very_big_positive_number_2).number,
                         self.result_divide_10)

        self.assertEqual((self.very_big_positive_number_1 / self.very_big_positive_number_2),
                         self.result_object_10)

        self.assertEqual((self.very_big_positive_number_3 / self.very_big_positive_number_4).number,
                         self.result_divide_11)

        self.assertEqual((self.very_big_positive_number_3 / self.very_big_positive_number_4),
                         self.result_object_11)

        self.assertEqual((self.very_big_positive_number_4 / self.very_big_positive_number_3).number,
                         self.result_divide_12)

        self.assertEqual((self.very_big_positive_number_4 / self.very_big_positive_number_3),
                         self.result_object_12)


if __name__ == '__main__':
    unittest.main()