import unittest
import sys, os
if os.name == 'posix':
    sep = '/'
else:
    sep = '\\'
sys.path.append(os.getcwd().rsplit(sep,1)[0])
from core import operations
from tests.util_decorators import timeout


class FunctionTest(unittest.TestCase):

    def setUp(self) -> None:
        self.big_positive_number_1 = "19884313212139312328923"
        self.big_positive_number_2 = "2920193810920129831098230192830"
        self.result_difference_1 = "2920193791035816618958917863907"

        self.big_positive_number_3 = "10000000000000000000000000"
        self.big_positive_number_4 = "90000000000000000000000000000000000000000000000000"
        self.result_difference_2 = "89999999999999999999999990000000000000000000000000"

        self.small_positive_number_1 = "73912873"
        self.small_positive_number_2 = "831212"
        self.result_difference_3 = "73081661"

        self.small_positive_number_3 = "80201"
        self.small_positive_number_4 = "932"
        self.result_difference_4 = "79269"

        self.very_big_positive_number_1 = "123" * 100
        self.very_big_positive_number_2 = "256" * 101
        self.result_difference_5 = ""

        self.very_big_positive_number_3 = "1938" * 10000
        self.very_big_positive_number_4 = "256" * 10101
        self.result_difference_6 = ""

        self.negative_number_1 = "-1329320320"
        self.negative_number_2 = "-372392729"

        self.positive_number_1 = "123903123312"
        self.positive_number_2 = "2312931231233123"

        self.wrong_char_number_1 = "dq3282023ds"
        self.wrong_char_number_2 = "dsajdl3228320das"

        self.test_object = operations.Operations()


    @timeout(3)
    def test_big_positive_numbers(self):
        self.assertEqual(self.test_object.difference_numbers(first_number=self.big_positive_number_2,
                                                             second_number=self.big_positive_number_1),
                         self.result_difference_1)

        self.assertEqual(self.test_object.difference_numbers(first_number=self.big_positive_number_4,
                                                             second_number=self.big_positive_number_3),
                         self.result_difference_2)

    def test_big_positive_numbers_negative_result(self):
        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.big_positive_number_1,
                                                second_number=self.big_positive_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.big_positive_number_3,
                                                second_number=self.big_positive_number_4)

    @timeout(3)
    def test_small_positive_numbers(self):
        self.assertEqual(self.test_object.difference_numbers(first_number=self.small_positive_number_1,
                                                             second_number=self.small_positive_number_2),
                         self.result_difference_3)

        self.assertEqual(self.test_object.difference_numbers(first_number=self.small_positive_number_3,
                                                             second_number=self.small_positive_number_4),
                         self.result_difference_4)

    @timeout(3)
    def test_small_positive_numbers_negative_result(self):
        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.small_positive_number_2,
                                                second_number=self.small_positive_number_1)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.small_positive_number_4,
                                                second_number=self.small_positive_number_3)

    @timeout(3)
    def test_very_big_positive_numbers(self):
        self.assertEqual(self.test_object.difference_numbers(first_number=self.very_big_positive_number_2,
                                                             second_number=self.very_big_positive_number_1),
                         self.result_difference_5)

        self.assertEqual(self.test_object.difference_numbers(first_number=self.very_big_positive_number_3,
                                                             second_number=self.very_big_positive_number_4),
                         self.result_difference_6)

    @timeout(3)
    def test_very_big_positive_numbers_negative_result(self):
        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.very_big_positive_number_1,
                                                second_number=self.very_big_positive_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.small_positive_number_4,
                                                second_number=self.small_positive_number_3)

    @timeout(3)
    def test_negative_numbers(self):
        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.negative_number_1,
                                                second_number=self.negative_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.negative_number_1,
                                                second_number=self.positive_number_1)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.negative_number_2,
                                                second_number=self.positive_number_1)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.negative_number_1,
                                                second_number=self.positive_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.negative_number_2,
                                                second_number=self.positive_number_2)

    @timeout(3)
    def test_wrong_numbers(self):
        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.wrong_char_number_1,
                                                second_number=self.wrong_char_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.wrong_char_number_1,
                                                second_number=self.positive_number_1)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.wrong_char_number_2,
                                                second_number=self.positive_number_1)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.wrong_char_number_1,
                                                second_number=self.positive_number_2)

        with self.assertRaises(ValueError):
            self.test_object.difference_numbers(first_number=self.wrong_char_number_2,
                                                second_number=self.positive_number_2)


if __name__ == '__main__':
    unittest.main()